let p = msg.payload;

if (!p.voltagePhase1)
    return null;

for (var prop in p) {
    if (!p[prop])
        p[prop] = 0;
}

// Meassurement
let data = "power";

// Tag set
data += ",location=test";
data += ",currency=" + p.currency;

// Field set
data += " power=" + p.power;
data += ",lastMeterConsumption=" + p.lastMeterConsumption;
data += ",accumulatedConsumption=" + p.accumulatedConsumption;
data += ",accumulatedProduction=" + p.accumulatedProduction;
data += ",accumulatedCost=" + p.accumulatedCost;
data += ",accumulatedReward=" + p.accumulatedReward;
data += ",minPower=" + p.minPower;
data += ",averagePower=" + p.averagePower;
data += ",maxPower=" + p.maxPower;
data += ",powerProduction=" + p.powerProduction;
data += ",minPowerProduction=" + p.minPowerProduction;
data += ",maxPowerProduction=" + p.maxPowerProduction;
data += ",lastMeterProduction=" + p.lastMeterProduction;
data += ",powerFactor=" + p.powerFactor;
data += ",voltagePhase1=" + p.voltagePhase1;
data += ",voltagePhase2=" + p.voltagePhase2;
data += ",voltagePhase3=" + p.voltagePhase3;
data += ",currentL1=" + p.currentL1;
data += ",currentL2=" + p.currentL2;
data += ",currentL3=" + p.currentL3;

msg.payload = data;

return msg;

let pl = msg.payload;

let prices = pl.viewer.homes[0].currentSubscription.priceInfo.today;

// Meassurement
let data = "";

for(let i = 0; i < prices.length; i++)
{
    let p = prices[i];
    data += "energy";

    // Tag set
    data += ",location=test";
    data += ",currency=" + p.currency;

    // Field set
    data += " total=" + p.total;
    data += ",energy=" + p.energy;
    data += ",tax=" + p.tax;
    data += ",level=\"" + p.level + "\"";
    data += " " + new Date(p.startsAt).getTime() / 1000 + "";
    data += "\n";
}

msg.payload = data;

return msg;
